import react from 'react';
import useForm from '../../utils/useForm';



const Login = ()=> {


    const onEnter = e => {
        e.stopPropagation();
        if (e.key === 'Enter') {
          loginHandler();
        }
      }

    const loginHandler = () => {
        console.log(inputValues);
    }



    const [inputValues, onChangeHandler, onSubmitHandler] = useForm(loginHandler);



return(


    <section>
      <div className="w-600">
        <div>
          {/* <LoginBanner> */}
            {/* <Icon className="icon-cloudapp" color="#fff" /> */}
          {/* </LoginBanner> */}
          <div className="bgWhite pa30301530">
            <h3>
              Login
            </h3>
            <h4>
              Welcome to Easy Ticket
            </h4>
            <div className="height20"/>
            <input type="text" 
            name="username" 
            onChange={onChangeHandler} 
            value={inputValues.username || ""}  

        />
        <input type="password" 
        name="password" 
        onChange={onChangeHandler} 
        value={inputValues.password || ""}  
        onKeyPress={onEnter}
        />
            <div className="height10"/>
            {/* {loginError && (
              <div>
                Invalid Credentials 
              </div>
            )} */}
            <button type="submit" onClick={loginHandler}>
              Submit 
            </button>

            <div className="footer">
              <p>
                Powered by Easy Ticket
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>

)
}


export default Login;
