import react from 'react';
import useForm from '../../utils/useForm';



const Login = ()=> {


    const onEnter = e => {
        e.stopPropagation();
        if (e.key === 'Enter') {
          loginHandler();
        }
      }

    const loginHandler = () => {
        console.log(inputValues);
    }



    const [inputValues, onChangeHandler, onSubmitHandler] = useForm(loginHandler);



return(


    <section>
      <div className="w-600 d-flex alc">
        {/* <div> */}
          {/* <LoginBanner> */}
            {/* <Icon className="icon-cloudapp" color="#fff" /> */}
          {/* </LoginBanner> */}
          <div className="bgWhite pa30301530">
            <h3>
              Login
            </h3>
            <h4>
              Welcome to Easy Ticket
            </h4>
            <div className="h-20"/>
            <div className="w-100 pos-rel m-15 wrapper">
            <label> staff Id Number</label>
            <input type="text" 
            className="form-control"
            name="username" 
            onChange={onChangeHandler} 
            value={inputValues.username || ""}  

        />

        </div>
        <div className="w-100 pos-rel m-15 wrapper">
            <label> Password</label>
        <input type="password" 
        className="form-control"
        name="password" 
        onChange={onChangeHandler} 
        value={inputValues.password || ""}  
        onKeyPress={onEnter}
        />

        </div>
            <div className="h-10"/>
            <button type="submit" onClick={loginHandler}>
              Submit 
            </button>

            <div className="footer">
              <p>
                Powered by Easy Ticket
              </p>
            </div>
          </div>


        {/* </div> */}
        <div className="login__bg">

          </div>
      </div>
    </section>

)
}


export default Login;
