import * as React from "react";
import ConfirmationNumberIcon from '@mui/icons-material/ConfirmationNumber';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Slide from '@mui/material/Slide';



const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
  });
const ValidateTicket = () => {

    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };

    const handleClose = () => {
      setOpen(false);
    };



  return (
    <section>
     <section className="page__header">
        <ConfirmationNumberIcon />
        <h3>Validate Ticket</h3>
      </section>
      <div className="s-divider"></div>
      <section>
      <div style={{margin: '13% 0'}}>
                        <input
                        type="text"
                        className="validate_ticket_form"
                        name="ticket_number"
                        placeholder="Enter ticket number"
                        />
                        <button className="btn btn-primary p-25 reposition_btn" onClick={handleClickOpen}> Search</button>
                    </div>

      </section>




      <div>
      <Dialog
        open={open}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleClose}
        aria-describedby="alert-dialog-slide-description"
      >
        <DialogTitle>{"Validate Confirmation"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-slide-description">
          <section className="flex-container" style={{margin: '20px 0'}}>
                    <div className="bus__card">
                        <div className="bus__image">
                        {/* <img src={BusImage} className="w-100"/> */}
                        </div>
                        <div className="bus__details pd-30px">
                            <p className="bus__type">Toyota - (Hiace)</p>
                            <div className="d-flex alc f-10 flex-start mt-15">
                                <div className="user__avatar bg-error">
                                    <h3>PY</h3>
                                </div>
                                <div className="lheight13">
                                <h4 className="f-300">Adeze Obi</h4>
                                <p className="sub__title">Driver</p>
                                </div>
                                </div>
                                <div className="s-divider my-10"></div>
                                <div className="bus__footer flex-container alc">
                                    <div>
                                        <p>Passengers</p>
                                        <h3>50</h3>
                                    </div>
                                    <div>
                                        <p>Seats</p>
                                        <h3>15</h3>
                                    </div>
                                </div>
                                <button className="btn btn-primary p-25 reposition_btn" onClick={handleClose}> Print ticket</button>
        <button className="btn btn-primary p-25 reposition_btn" onClick={handleClose}> Confirm ticket</button>
                        </div>

                    </div>
      </section>
          </DialogContentText>
        </DialogContent>
        {/* <DialogActions>
        <button className="btn btn-primary p-25 reposition_btn" onClick={handleClose}> Print</button>
        <button className="btn btn-primary p-25 reposition_btn" onClick={handleClose}> Confirm</button>
        </DialogActions> */}
      </Dialog>
    </div>

    </section>
  );
};

export default ValidateTicket;
